 81	 private Icon getIcon() {
 82	 Color lightColor = FlatUIUtils.getUIColor("Menu.icon.lightColor", Color.red);
 83	 Color darkColor = FlatUIUtils.getUIColor("Menu.icon.darkColor", Color.red);
 84	 FlatSVGIcon icon = new FlatSVGIcon("icon/menu" + menuIndex + ".svg");
 85	 FlatSVGIcon.ColorFilter f = new FlatSVGIcon.ColorFilter();
 86	 f.add(Color.decode("#969696"), lightColor, darkColor);
 87	 icon.setColorFilter(f);
 88	 return icon;
