 125	 protected void setSelectedIndex(int index) {
 126	 int size = getComponentCount();
 127	 boolean selected = false;
 128	 for (int i = 0; i < size; i++) {
 129	 Component com = getComponent(i);
 130	 if (com instanceof JButton) {
 131	 ((JButton) com).setSelected(i == index);
 132	 if (i == index) {
 133	 selected = true;
 134	 }
 135	 }
 136	 }
 137	 ((JButton) getComponent(0)).setSelected(selected);
 138	 popup.setSelectedIndex(index);
 139	 }
