 7	import com.formdev.flatlaf.util.Animator;
 8	import java.util.HashMap;
 9	
 10	/**
 11	 *
 12	 * @author PhamTra
 13	 */
 14	public class MenuAnimation {
 15	
 16	 private static final HashMap<MenuItem, Animator> hash = new HashMap<>();
 17	
 18	 public static void animate(MenuItem menu, boolean show) {
 19	 if (hash.containsKey(menu) && hash.get(menu).isRunning()) {
 20	 hash.get(menu).stop();
 21	 }
 22	 menu.setMenuShow(show);
 23	 Animator animator = new Animator(400, new Animator.TimingTarget() {
 24	 @Override
 25	 public void timingEvent(float f) {
 26	 if (show) {
 27	 menu.setAnimate(f);
 28	 } else {
 29	 menu.setAnimate(1f - f);
 30	 }
 31	 menu.revalidate();
 32	 }
 33	
 34	 @Override
 35	 public void end() {
 36	 hash.remove(menu);
 37	 }
 38	 });
 39	 animator.setResolution(1);
 40	 animator.setInterpolator((float f) -> (float) (1 - Math.pow(1 - f, 3)));
 41	 animator.start();
 42	 hash.put(menu, animator);
 43	 }
 44	}
