 91	 private void setMenuFull(boolean full) {
 92	 String icon;
 93	 if (getComponentOrientation().isLeftToRight()) {
 94	 icon = (full) ? "menu_left.svg" : "menu_right.svg";
 95	 } else {
 96	 icon = (full) ? "menu_right.svg" : "menu_left.svg";
 97	 }
 98	 menuButton.setIcon(new FlatSVGIcon("icon/svg/" + icon, 0.8f));
 99	 menu.setMenuFull(full);
 100	 revalidate();
 101	 }
 102	
