 91	 private void init() {
 92	 setLayout(new MenuLayout());
 93	 putClientProperty(FlatClientProperties.STYLE, ""
 94	 + "background:$Menu.background;"
 95	 + "foreground:$Menu.lineColor");
 96	 for (int i = 0; i < menus.length; i++) {
 97	 JButton menuItem = createButtonItem(menus[i]);
 98	 menuItem.setHorizontalAlignment(menuItem.getComponentOrientation().isLeftToRight() ? JButton.LEADING : JButton.TRAILING);
 99	 if (i == 0) {
 100	 menuItem.setIcon(getIcon());
 101	 menuItem.addActionListener((ActionEvent e) -> {
 102	 if (menus.length > 1) {
 103	 if (menu.isMenuFull()) {
 104	 MenuAnimation.animate(MenuItem.this, !menuShow);
 105	 } else {
 106	 popup.show(MenuItem.this, (int) MenuItem.this.getWidth() + UIScale.scale(5), UIScale.scale(menuItemHeight) / 2);
 107	 }
 108	 } else {
 109	 menu.runEvent(menuIndex, 0);
 110	 }
 111	 });
