 127	 private class AccentColorIcon extends FlatAbstractIcon {
 128	
 129	 private final String colorKey;
 130	
 131	 public AccentColorIcon(String colorKey) {
 132	 super(16, 16, null);
 133	 this.colorKey = colorKey;
 134	 }
 135	
 136	 @Override
 137	 protected void paintIcon(Component c, Graphics2D g) {
 138	 Color accColor = UIManager.getColor(colorKey);
 139	 if (accColor == null) {
 140	 accColor = Color.lightGray;
 141	 } else if (!c.isEnabled()) {
 142	 accColor = FlatLaf.isLafDark()
 143	 ? ColorFunctions.shade(accColor, 0.5f)
 144	 : ColorFunctions.tint(accColor, 0.6f);
 145	 }
 146	 g.setColor(accColor);
 147	 g.fillRoundRect(1, 1, width - 2, height - 2, 5, 5);
 148	 }
 149	 }
 150	}
