 104	 private void colorAccentChanged(String colorKey) {
 105	 if (popup.isVisible()) {
 106	 popup.setVisible(false);
 107	 }
 108	 Color color = UIManager.getColor(colorKey);
 109	 selectedButton.setIcon(new AccentColorIcon(colorKey));
 110	 Class<? extends LookAndFeel> lafClass = UIManager.getLookAndFeel().getClass();
 111	 try {
 112	 FlatLaf.setGlobalExtraDefaults(Collections.singletonMap("@accentColor", toHexCode(color)));
 113	 FlatLaf.setup(lafClass.newInstance());
 114	 FlatLaf.updateUI();
 115	 } catch (InstantiationException | IllegalAccessException ex) {
 116	 LoggingFacade.INSTANCE.logSevere(null, ex);
 117	 }
 118	 }
